```typescript
// app/(tabs)/explore/[category].tsx
import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, FlatList, TouchableOpacity } from "react-native";
import { useTheme } from "@react-navigation/native";
import { useLocalSearchParams, useRouter } from "expo-router";
import { CreaturesData } from "@/data/paranormal/creatures"; // Example, replace with dynamic import
import { UFOsData } from "@/data/paranormal/ufos";
import { GhostsData } from "@/data/paranormal/ghosts";

const categoryDataMap = {
  creatures: CreaturesData,
  ufos: UFOsData,
  ghosts: GhostsData,
  // ... add other categories
};

export default function CategoryScreen() {
  const theme = useTheme();
  const { category } = useLocalSearchParams();
  const router = useRouter();
  const [categoryData, setCategoryData] = useState(null);

  useEffect(() => {
    const loadCategoryData = async () => {
      // Dynamically import the category data based on the route param
      // For simplicity, I'm using the categoryDataMap.  A more robust solution
      // would involve dynamic imports.
      const data = categoryDataMap[category];
      setCategoryData(data);
    };

    loadCategoryData();
  }, [category]);

  if (!categoryData) {
    return (
      <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
        <Text style={{ color: theme.colors.text }}>Loading...</Text>
      </View>
    );
  }

  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={[styles.topicCard, { backgroundColor: theme.colors.card }]}
      onPress={() => router.push(`/explore/${category}/${item.name}`)}
    >
      <Text style={[styles.topicName, { color: theme.colors.text }]}>{item.name}</Text>
      <Text style={[styles.topicDescription, { color: theme.colors.text }]}>{item.description}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Text style={[styles.categoryTitle, { color: theme.colors.text }]}>{categoryData.category}</Text>
      <FlatList
        data={categoryData.topics}
        renderItem={renderItem}
        keyExtractor={(item) => item.name}
        contentContainerStyle={styles.listContainer}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
  },
  categoryTitle: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 10,
  },
  listContainer: {
    paddingBottom: 20,
  },
  topicCard: {
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
  },
  topicName: {
    fontSize: 18,
    fontWeight: "bold",
  },
  topicDescription: {
    fontSize: 14,
  },
});
```

